mapper.select:
select 

,

from ujn_content
where status = '1' and column_id = #{column_id,jdbcType=VARCHAR}
and unix_timestamp(validity_time) <  unix_timestamp(now()) 
order by #{order_column} #{order_type} limit #{count_num}

mapper.update:
